mapper.select:
	select p.prdt_no, p.prdt_name, o.option_no, o.option_contents, p.price, 
		   p.delivery_price, p.thumbnail_name, p.thumbnail_path,
		   (p.price * #{buy_qty}) + p.delivery_price total_price, #{buy_qty} as buy_qty
		   from product p, prdt_option o
		   where p.prdt_no = #{prdt_no}
           and o.option_no = #{option_no}

select mem_no, mem_name, pnum, post_code, add_1, add_2
from member
where mem_no = #{mem_no}

select b.basket_no, p.prdt_no, p.prdt_name, o.option_no, o.option_contents, p.price, p.delivery_price, p.thumbnail_name, p.thumbnail_path, (p.price * b.buy_qty) + p.delivery_price total_price, b.buy_qty from product p, prdt_option o, basket b where b.basket_no in
#{basket_no}
and p.prdt_no = o.prdt_no and p.prdt_no = b.prdt_no and b.option_no = o.option_no order by b.reg_date desc

mapper.insert:
insert into order_main (mem_no, prdt_cnt, total_pay_amt, order_date) 
values (#{mem_no}, #{prdt_cnt}, #{total_pay_amt}, now());

	select LAST_INSERT_ID()

insert into order_detail (order_no, prdt_no, prdt_price, option_no, prdt_delivery_price, buy_qty, pay_amt) select #{order_no}, p.prdt_no, p.price, b.option_no, p.delivery_price, b.buy_qty, (p.price * b.buy_qty + p.delivery_price) from product p, basket b where b.basket_no in
#{basket_no}
and b.prdt_no = p.prdt_no order by b.reg_date

mapper.delete:
delete from basket b
where b.basket_no in

	#{basket_no}
